import { useEffect, useState } from "react";
import { useSetState } from "react-use";
import {createContext} from 'react';

export const ItemContext = createContext();

const ArrayMap = () => {
  const [hobbies, setHobbies] = useState([
    "I am level 38 and counting!"
  ]);

const getHobbies = async() => {
  if (hobbies.length < 3){
    hobbies.push("i  am on team valor");
    hobbies.push("add me! my trainer code is: 0126 9490 6285");
  }
  return hobbies
}

  useEffect(() => {
    if (hobbies.length < 3){
      getHobbies().then((hobbies) => {
      setHobbies(hobbies);
      });
    }
  });

  getHobbies();
  return (
    <>
      {hobbies.map((description, index) => {
        console.log({ index, description});
        return (
          <ul>
            <li>{description}</li>
          </ul>
        );
      })}
    </>
  );
};


function App() {
  return (
    <div className="App">
      <header className="App-header">
        <p>
          Hi!
        </p>
      </header>
    </div>
  );

  return (
    <>
    <ItemContext.Provider
    value={{ items, setItems}}
  > 
  { /* We use Context.Provider to specify what values we are putting into context */ }
  <Child /> 
  { /* Notice that we are not passing no props to our child */ }
  <Child />
  </ItemContext.Provider> 
  { /* Everything within these Provider tags will be able to access our context */ }
    </>
)
}

export default App;
